import { createSlice } from '@reduxjs/toolkit'
const Admdata = createSlice({
    name: 'AdminData',
    initialState: {
        AllDataAd: [],
        SiDAta: [],
    },
    reducers: {

        AlldataSl: (state, action) => {
            console.log("Alldata--", action.payload);

            state.AllDataAd.push(action.payload)

        },
        SiData: (state, action) => {
            console.log("Sidata--", action.payload);
            state.SiDAta.push(action.payload)
        },
        dataRemove: (state, action) => {
            state.AllDataAd = []
        }
    }


})
export const { AlldataSl, SiData, dataRemove } = Admdata.actions
export default Admdata.reducer
