import {createSlice} from '@reduxjs/toolkit'
const userdata=createSlice({
    name:'AdminData',
    initialState:{
        
        AllDataAd:[],
        AllDataID:[],
        SiDAta:[]

    },
    reducers:{
      
        AlldataSl:(state,action)=>{
        console.log("Alldata--",action.payload);
        console.log("AlldataAd",state.AllDataAd);
        console.log("Alldata",state.AllDataAd);

        action.payload.map((ap)=>{
            if(!state.AllDataID.includes(ap._id) ){
                state.AllDataID.push(ap._id)
                state.AllDataAd.push(ap)
                console.log("AlldataAd==",ap);
                console.log("AlldataAd==",ap);

            }
        })
            // state.AllDataAd.push(action.payload)
        },
        SiData:(state,action)=>{
            console.log("Sidata--",action.payload);
            state.SiDAta.push(action.payload)
        },
        dataRemove:(state,action)=>{
            state.AllDataAd=[]
        }
    }
})

export const {AlldataSl,SiData,dataRemove}=userdata.actions
export default userdata.reducer
